    border-top-color: #ff6f61 !important;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.07),
    inset 0 1px 2px rgba(0, 0, 0, 0.01),
    0 0 10px rgba(255, 111, 97, 0.15);
  }
